import React from 'react';

const Invoice = ({ invoice, companyName = "Your Business Name" }) => {
  const currentDate = new Date();
  const issueDate = invoice?.date ? new Date(invoice.date) : currentDate;
  const dueDate = new Date(issueDate);
  dueDate.setDate(dueDate.getDate() + 15); // 15 days from issue date

  const formatDate = (date) => {
    return date.toLocaleDateString('en-GB', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const subtotal = invoice?.items?.reduce((sum, item) => sum + (item.qty * item.price), 0) || 0;
  const gst10 = subtotal * 0.1; // 10% GST
  const total = subtotal + gst10;

  return (
    <div style={styles.invoiceContainer}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.businessInfo}>
          <div style={styles.logo}>
            <div style={styles.logoIcon}>🏢</div>
          </div>
          <div style={styles.businessName}>{companyName}</div>
        </div>
        <div style={styles.invoiceTitle}>
          <div style={styles.invoiceNumber}>Invoice {invoice?.id || '2022435'}</div>
          <div style={styles.taxInvoice}>Tax invoice</div>
        </div>
      </div>

      {/* Client and Invoice Details */}
      <div style={styles.detailsSection}>
        <div style={styles.billTo}>
          <div style={styles.billToLabel}>BILL TO</div>
          <div style={styles.clientName}>{invoice?.customer?.name || 'Your Client'}</div>
          <div style={styles.clientAddress}>{invoice?.customer?.address || '100 Harris St'}</div>
          <div style={styles.clientAddress}>Sydney NSW NSW 2009</div>
          <div style={styles.clientAddress}>Australia</div>
        </div>
        <div style={styles.invoiceDetails}>
          <div style={styles.detailRow}>
            <span>Issue date:</span>
            <span style={styles.detailValue}>{formatDate(issueDate)}</span>
          </div>
          <div style={styles.detailRow}>
            <span>Due date:</span>
            <span style={styles.detailValue}>{formatDate(dueDate)}</span>
          </div>
          <div style={styles.detailRow}>
            <span>Reference:</span>
            <span style={styles.detailValue}>{invoice?.id || '2022435'}</span>
          </div>
        </div>
      </div>

      {/* Summary Bar */}
      <div style={styles.summaryBar}>
        <div style={styles.summaryItem}>
          <div style={styles.summaryLabel}>Invoice No.</div>
          <div style={styles.summaryValue}>{invoice?.id || '2022435'}</div>
        </div>
        <div style={styles.summaryItem}>
          <div style={styles.summaryLabel}>Issue date</div>
          <div style={styles.summaryValue}>{formatDate(issueDate)}</div>
        </div>
        <div style={styles.summaryItem}>
          <div style={styles.summaryLabel}>Due date</div>
          <div style={styles.summaryValue}>{formatDate(dueDate)}</div>
        </div>
        <div style={styles.summaryItemTotal}>
          <div style={styles.summaryLabel}>Total due (AUD)</div>
          <div style={styles.summaryValueTotal}>{formatCurrency(total)}</div>
        </div>
      </div>

      {/* Items Table */}
      <div style={styles.tableContainer}>
        <table style={styles.itemsTable}>
          <thead>
            <tr style={styles.tableHeader}>
              <th style={styles.tableHeaderCell}>Description</th>
              <th style={styles.tableHeaderCellQty}>Quantity</th>
              <th style={styles.tableHeaderCellPrice}>Unit price ($)</th>
              <th style={styles.tableHeaderCellAmount}>Amount ($)</th>
            </tr>
          </thead>
          <tbody>
            {invoice?.items?.length > 0 ? (
              invoice.items.map((item, index) => (
                <tr key={index} style={styles.tableRow}>
                  <td style={styles.tableCell}>{item.name}</td>
                  <td style={styles.tableCellQty}>{item.qty}</td>
                  <td style={styles.tableCellPrice}>{formatCurrency(item.price)}</td>
                  <td style={styles.tableCellAmount}>{formatCurrency(item.qty * item.price)}</td>
                </tr>
              ))
            ) : (
              <>
                <tr style={styles.tableRow}>
                  <td style={styles.tableCell}>Services & products</td>
                  <td style={styles.tableCellQty}>1</td>
                  <td style={styles.tableCellPrice}>$100.00</td>
                  <td style={styles.tableCellAmount}>$100.00</td>
                </tr>
                <tr style={styles.tableRow}>
                  <td style={styles.tableCell}>More services & products</td>
                  <td style={styles.tableCellQty}>1</td>
                  <td style={styles.tableCellPrice}>$2,000.00</td>
                  <td style={styles.tableCellAmount}>$2,000.00</td>
                </tr>
              </>
            )}
          </tbody>
        </table>
      </div>

      {/* Totals Section */}
      <div style={styles.totalsSection}>
        <div style={styles.totalsContainer}>
          <div style={styles.totalRow}>
            <span>Subtotal:</span>
            <span style={styles.totalAmount}>{formatCurrency(subtotal)}</span>
          </div>
          <div style={styles.totalRow}>
            <span>GST 10% from $100.00:</span>
            <span style={styles.totalAmount}>{formatCurrency(gst10)}</span>
          </div>
          <div style={styles.totalRow}>
            <span>GST 20% from $2,000.00:</span>
            <span style={styles.totalAmount}>{formatCurrency(0)}</span>
          </div>
          <div style={styles.totalRowFinal}>
            <span style={styles.totalLabel}>Total (AUD):</span>
            <span style={styles.totalAmountFinal}>{formatCurrency(total)}</span>
          </div>
        </div>
      </div>

      {/* Signature Section */}
      <div style={styles.signatureSection}>
        <div style={styles.signatureLabel}>Issued by, signature:</div>
        <div style={styles.signature}>Ron Paul</div>
      </div>

      {/* Footer */}
      <div style={styles.footer}>
        <div style={styles.footerLeft}>
          <div style={styles.contactItem}>📞 +61200000000</div>
          <div style={styles.businessNameFooter}>{companyName}</div>
          <div style={styles.addressLine}>5 Martin Pl</div>
          <div style={styles.addressLine}>Sydney NSW 2000</div>
          <div style={styles.addressLine}>Australia</div>
        </div>
        <div style={styles.footerCenter}>
          <div style={styles.contactItem}>🌐 www.yourbusinessname.com.au</div>
        </div>
        <div style={styles.footerRight}>
          <div style={styles.contactItem}>✉️ email@yourbusinessname.com.au</div>
        </div>
      </div>
    </div>
  );
};

