import React from 'react';

const Invoice = ({ invoice, companyName = "Your Business Name" }) => {
  const currentDate = new Date();
  const issueDate = invoice?.date ? new Date(invoice.date) : currentDate;
  const dueDate = new Date(issueDate);
  dueDate.setDate(dueDate.getDate() + 15); // 15 days from issue date

  const formatDate = (date) => {
    return date.toLocaleDateString('en-GB', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const subtotal = invoice?.items?.reduce((sum, item) => sum + (item.qty * item.price), 0) || 0;
  const tax10 = subtotal * 0.1; // 10% Tax
  const total = subtotal + tax10;

  return (
    <div style={styles.invoiceContainer}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.businessInfo}>
          <div style={styles.businessName}>{companyName}</div>
          <div style={styles.businessAddress}>
            <div>14B, Northern Street</div>
            <div>Greater South Avenue</div>
            <div>New York 10001</div>
            <div>U.S.A</div>
          </div>
        </div>
        <div style={styles.invoiceTitle}>
          <div style={styles.invoiceTitleText}>INVOICE</div>
          <div style={styles.invoiceNumber}># {invoice?.id || 'INV-000011'}</div>
          <div style={styles.balanceDue}>
            <div style={styles.balanceDueLabel}>Balance Due</div>
            <div style={styles.balanceDueAmount}>{formatCurrency(total)}</div>
          </div>
        </div>
      </div>

      {/* Bill To and Invoice Details */}
      <div style={styles.detailsSection}>
        <div style={styles.billTo}>
          <div style={styles.billToLabel}>Bill To</div>
          <div style={styles.clientName}>{invoice?.customer?.name || 'Jack Little'}</div>
          <div style={styles.clientAddress}>{invoice?.customer?.address || '3242 Chandler Hollow Road'}</div>
          <div style={styles.clientAddress}>Pittsburgh</div>
          <div style={styles.clientAddress}>15222 Pennsylvania</div>
        </div>
        <div style={styles.invoiceDetails}>
          <div style={styles.detailRow}>
            <span>Invoice Date :</span>
            <span style={styles.detailValue}>{formatDate(issueDate)}</span>
          </div>
          <div style={styles.detailRow}>
            <span>Terms :</span>
            <span style={styles.detailValue}>Due on Receipt</span>
          </div>
        </div>
      </div>

      {/* Items Table */}
      <div style={styles.tableContainer}>
        <table style={styles.itemsTable}>
          <thead>
            <tr style={styles.tableHeader}>
              <th style={styles.tableHeaderCell}>#</th>
              <th style={styles.tableHeaderCellDesc}>Item & Description</th>
              <th style={styles.tableHeaderCellQty}>Qty</th>
              <th style={styles.tableHeaderCellPrice}>Rate</th>
              <th style={styles.tableHeaderCellAmount}>Amount</th>
            </tr>
          </thead>
          <tbody>
            {invoice?.items?.length > 0 ? (
              invoice.items.map((item, index) => (
                <tr key={index} style={styles.tableRow}>
                  <td style={styles.tableCell}>{index + 1}</td>
                  <td style={styles.tableCellDesc}>
                    <div style={styles.itemName}>{item.name}</div>
                    <div style={styles.itemDescription}>Professional service delivery</div>
                  </td>
                  <td style={styles.tableCellQty}>
                    <div style={styles.qtyNumber}>{item.qty.toFixed(2)}</div>
                    <div style={styles.qtyLabel}>Items</div>
                  </td>
                  <td style={styles.tableCellPrice}>{formatCurrency(item.price)}</td>
                  <td style={styles.tableCellAmount}>{formatCurrency(item.qty * item.price)}</td>
                </tr>
              ))
            ) : (
              <>
                <tr style={styles.tableRow}>
                  <td style={styles.tableCell}>1</td>
                  <td style={styles.tableCellDesc}>
                    <div style={styles.itemName}>Social Media Content</div>
                    <div style={styles.itemDescription}>Engaging and share-worthy social media posts that captivate your audience and drive engagement</div>
                  </td>
                  <td style={styles.tableCellQty}>
                    <div style={styles.qtyNumber}>65.00</div>
                    <div style={styles.qtyLabel}>Posts</div>
                  </td>
                  <td style={styles.tableCellPrice}>{formatCurrency(30)}</td>
                  <td style={styles.tableCellAmount}>{formatCurrency(1950)}</td>
                </tr>
                <tr style={styles.tableRow}>
                  <td style={styles.tableCell}>2</td>
                  <td style={styles.tableCellDesc}>
                    <div style={styles.itemName}>Email Newsletter Writing</div>
                    <div style={styles.itemDescription}>Engaging and informative email newsletters that keep your subscribers informed and connected</div>
                  </td>
                  <td style={styles.tableCellQty}>
                    <div style={styles.qtyNumber}>20.00</div>
                    <div style={styles.qtyLabel}>Emails</div>
                  </td>
                  <td style={styles.tableCellPrice}>{formatCurrency(99)}</td>
                  <td style={styles.tableCellAmount}>{formatCurrency(1980)}</td>
                </tr>
                <tr style={styles.tableRow}>
                  <td style={styles.tableCell}>3</td>
                  <td style={styles.tableCellDesc}>
                    <div style={styles.itemName}>Press Release Writing</div>
                    <div style={styles.itemDescription}>Professionally written press releases that effectively communicate your news and attract media attention</div>
                  </td>
                  <td style={styles.tableCellQty}>
                    <div style={styles.qtyNumber}>1.00</div>
                    <div style={styles.qtyLabel}>Package</div>
                  </td>
                  <td style={styles.tableCellPrice}>{formatCurrency(2500)}</td>
                  <td style={styles.tableCellAmount}>{formatCurrency(2500)}</td>
                </tr>
              </>
            )}
          </tbody>
        </table>
      </div>

      {/* Totals Section */}
      <div style={styles.totalsSection}>
        <div style={styles.totalsContainer}>
          <div style={styles.totalRow}>
            <span>Sub Total</span>
            <span style={styles.totalAmount}>{formatCurrency(subtotal)}</span>
          </div>
          <div style={styles.totalRow}>
            <span>Tax Rate (10%)</span>
            <span style={styles.totalAmount}>{formatCurrency(tax10)}</span>
          </div>
          <div style={styles.totalRowFinal}>
            <span style={styles.totalLabel}>Total</span>
            <span style={styles.totalAmountFinal}>{formatCurrency(total)}</span>
          </div>
          <div style={styles.balanceDueRow}>
            <span style={styles.balanceDueLabel}>Balance Due</span>
            <span style={styles.balanceDueAmount}>{formatCurrency(total)}</span>
          </div>
        </div>
      </div>

      {/* Notes and Terms */}
      <div style={styles.notesSection}>
        <div style={styles.notesContainer}>
          <div style={styles.notesTitle}>Notes</div>
          <div style={styles.notesContent}>Thanks for your business.</div>
        </div>
        <div style={styles.termsContainer}>
          <div style={styles.termsTitle}>Terms & Conditions</div>
          <div style={styles.termsContent}>All services provided are subject to the terms and conditions outlined in the agreement or engagement letter.</div>
        </div>
      </div>
    </div>
  );
};

